export class ResultadoRenderer{constructor(e){this.elemento=e}render(e){if(!this.elemento||!e)return;let r="";switch(e.type){case"result":r=this.renderizarResultado(e.data);break;case"table":r=this.renderizarTabla(e.data);break;case"list":r=this.renderizarLista(e.data);break;case"evaluation":r=this.renderizarEvaluacion(e.data);break;default:r=`<div class="resultado-error">Tipo de resultado no soportado: ${e.type}</div>`}this.elemento.innerHTML=r}renderizarResultado(e){return`\n            <div class="resultado-exito">\n                <strong>${e.label}:</strong>\n                ${e.value}\n            </div>\n        `}renderizarTabla(e){return`\n            <div class="resultado-exito">\n                <table>\n                    <thead>\n                        <tr>${e.headers.map(e=>`<th>${e}</th>`).join("")}</tr>\n                    </thead>\n                    <tbody>\n                        ${e.rows.map(e=>`<tr>${e.map(e=>`<td>${e}</td>`).join("")}</tr>`).join("")}\n                    </tbody>\n                </table>\n            </div>\n        `}renderizarLista(e){const r=e.valores.map(e=>`<li>${e}</li>`).join("");return`\n            <div class="resultado-exito">\n                <p><strong>${e.descripcion}:</strong></p>\n                <ul>\n                    ${r}\n                </ul>\n            </div>\n        `}renderizarEvaluacion(e){return`\n            <div class="${e.ok?"resultado-exito":"resultado-error"}">\n                ${e.message}\n            </div>\n        `}}//# sourceMappingURL=06-ResultadoRenderer.js.map
